targets.expr:
# Join metrics into a flat table of values per instance (instance, role, target_role, last_sync_success)
max by (instance, role) (
  stolon_cluster_identifier{cluster_name="$cluster"} * on (instance) group_left(role) (
    time() - stolon_keeper_last_sync_success_seconds * on(instance) group_left(role) (
      (stolon_keeper_local_role == 1) * on(instance) group_left(role) (
        label_replace(stolon_keeper_target_role == 1, "target_role", "$1", "role", "(.*)")
      )
    )
  )
)

max by (instance) (
  time() - process_start_time_seconds and (stolon_keeper_shutdown_seconds and on(instance) stolon_cluster_identifier{cluster_name="$cluster", component="keeper"})
)

max by (instance) (
  stolon_keeper_local_role and on(instance) stolon_cluster_identifier{cluster_name="$cluster", component="keeper"}
)
